Scripts.OnClick:
local textbox = getglobal("myTabPage6DecayPercent");
local decay = tonumber(textbox:GetText());
NOOBDKP_g_options["decay_percent"] = decay;
textbox:ClearFocus();
NoobDKP_Decay();

PanelTemplates_SetTab(noobMainFrame, 1);
myTabPage1:Show();
myTabPage2:Hide();
myTabPage3:Hide();
myTabPage4:Hide();
myTabPage5:Hide();
myTabPage6:Hide();

