myTabPage1:Hide();
myTabPage2:Hide();
myTabPage3:Hide();
myTabPage4:Show();
myTabPage5:Hide();
myTabPage6:Hide();

PanelTemplates_SetTab(noobMainFrame, 5);
myTabPage1:Hide();
myTabPage2:Hide();
myTabPage3:Hide();
myTabPage4:Hide();
myTabPage5:Show();
myTabPage6:Hide();

